mapper.select:
select 
from t_sale_chance


    customer_name like concat('%',#{saleChanceDTO.customerName,jdbcType=VARCHAR},'%')


    and create_man = #{userId}


    and state like concat('%',#{saleChanceDTO.state,jdbcType=INTEGER},'%')



limit #{start},#{saleChanceDTO.pageSize}

mapper.update:
update t_sale_chance
set dev_result = #{devResult}
where id = #{id}
